@Component({
  selector: 'zyfra-toggle-button',
  templateUrl: './zyfra-toggle-button.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ZyfraToggleButtonComponent {
  @Input() model = true;
  @Input() onLabel: string = 'confirm';
